from Manannikov_K_DZ_4.test_job_2.cbr_parser.utils import extract_dataimport decimaldef currency_rates(val_cod):    """ Функция принимает в качестве аргумента код валюты (например, USD, EUR, GBP, ...)и возвращающую курс этой валюты по отношению к рублю """    currency = dict(zip(extract_data('CharCode'), extract_data('Value')))  # создаёт словарь с ключами по коду валют    currency_get = currency.get(val_cod.upper())    currency_repl = decimal.Decimal(currency_get.replace(',', '.'))    return currency_repl